	// ***********************************************************************************
	// 把教务处请求的成绩数据转换
	// 成绩信息都在 items属性里 是一个jsonarray

	// bfzcj 分数 bj 班级 ，比如计科1602 jgmc 自己的学院 jsxm 教师姓名
	// kcmc 课程名字 ,比如算法分析与设计-0001，后面的数字我不知道什么意思
	// kclbmc 课程类型，比如 通识必修课、专业课(必修)、集中实践课程。通识选修课(通选)等 ksxz 考试的信息，比如正常考试
	// xm 自己的姓名 xh 自己的学号 xb 自己的性别 xf 学分
	public static Vector<ListViewScore> convertScore(String json2) {

		JSONObject jsonObject = JSONObject.fromObject(json2);
		String json = jsonObject.getString("items");

		JSONArray jsonArray = JSONArray.fromObject(json);

		Vector<ListViewScore> scores = new Vector<ListViewScore>();

		for (int i = 0; i < jsonArray.size(); ++i) {

			ListViewScore score = new ListViewScore();

			String jsonstr = jsonArray.getString(i);
			JSONObject jsonObject2 = JSONObject.fromObject(jsonstr);
			score.courseName = jsonObject2.getString("kcmc");
			score.courseScore = jsonObject2.getDouble("bfzcj");
			score.courseCredit = jsonObject2.getDouble("xf");
			/*
			 * score.setCourseScore(jsonObject2.getDouble("bfzcj"));
			 * score.setUserclass(jsonObject2.getString("bj"));
			 * score.setUsercollege(jsonObject2.getString("jgmc"));
			 * score.setCourseTeacher(jsonObject2.getString("jsxm"));
			 * 
			 * score.setCourseName(jsonObject2.getString("jxbmc"));
			 * score.setCourseType(jsonObject2.getString("kclbmc"));
			 * score.setExamInfo(jsonObject2.getString("ksxz"));
			 * 
			 * score.setUsername(jsonObject2.getString("xm"));
			 * score.setUsersex(jsonObject2.getString("xh"));
			 * score.setUserxuehao(jsonObject2.getString("xb"));
			 */

			// System.out.println(coursename+" "+score);
			scores.add(score);

		}
		return scores;

	}

	// ********************************************************************************
	// 课程有4种 实践 实验 理论 上机 对应json属性为 sjkList xqbzxxszList kbList

	// 1 实践 sjkList kcmc 课程名称，比如 计算机组成原理课程设计 qsz 开始时间，第几周 zzz 结束时间，第几周
	// zhxs 持续时间，几周 xm 教师名字

	// 2 实验 xqbzxxszList

	// 3 理论 kbList kcmc 课程名称 xqmc 上课所在校区，比如主校区 cdmc 上课地点，比如10教2302
	// jc 上课时间，比如 1-2节 xm 教师名字 xqjmc 这个课是周几
	// zcd 上课时间 ，比如 2-14周(双) zcmc 教师职称，比如讲师

	// 4 上机

	public static Vector<Course> convertKebiao(String json2) {

		JSONObject jsonObject = JSONObject.fromObject(json2);
		String json = jsonObject.getString("kbList");

		JSONArray jsonArray = JSONArray.fromObject(json);

		Vector<Course> courses = new Vector<Course>();

		// 拿到星期三上的课
		// System.out.println("星期一");
		// System.out.println();
		//
		for (int i = 0; i < jsonArray.size(); ++i) {

			String jsonstr = jsonArray.getString(i);
			JSONObject jsonObject2 = JSONObject.fromObject(jsonstr);

			// if(jsonObject2.getString("xqjmc").equals("星期一")) {
			// 这样写是因为 怕某课一天中上两次 而且数据方式为 1-2节,5-6节
			String string = jsonObject2.getString("jc");
			int index = 0;
			for (int j = 0; j < string.length(); ++j) {
				if (string.charAt(j) == '节') {
					Course course = new Course();
					course.setCN(jsonObject2.getString("kcmc"));
					;
					course.setCC(jsonObject2.getString("xqmc"));
					;
					course.setCP(jsonObject2.getString("cdmc"));
					;
					course.setCT1(jsonObject2.getString("zcd"));
					;
					course.setCT2(jsonObject2.getString("xqjmc"));
					;
					course.setCT3(string.substring(index, j + 1));
					;
					course.setCTN(jsonObject2.getString("xm"));
					;
					courses.add(course);
					for (int k = j + 1; k < string.length(); ++k) {
						if (MyTools.isInteger(string.charAt(k) + "")) {
							index = k;
							break;
						}
					}
				}
			}

			// System.out.println(kcmc+" "+xqmc+" "+cdmc+" "+zcd+" "+jc+" "+xm);
		}

		return courses;
	}

	
	//json转化为教室信息
	public static Vector<ClassRoom> covertClassRoom(String jsonstr) {

		
		JSONObject jsonObject = JSONObject.fromObject(jsonstr);
		String json = jsonObject.getString("items");

		JSONArray jsonArray = JSONArray.fromObject(json);

		Vector<ClassRoom> classRooms = new Vector<ClassRoom>();
		
		for (int i = 0; i < jsonArray.size(); ++i) {

			json = jsonArray.getString(i);
			JSONObject jsonObject2 = JSONObject.fromObject(json);
			
			ClassRoom classRoom = new ClassRoom();
			classRoom.setRoomName(jsonObject2.getString("cdmc"));
			classRoom.setRoomType(jsonObject2.getString("cdlbmc"));
			classRoom.setSeatNum(jsonObject2.getInt("zws"));
			
			classRooms.add(classRoom);
			
		}
		
		
		return classRooms;
	}

